import React, { useEffect, useState } from 'react';
import './PokemonCard.css'

const PokemonCard = () => {
    const [pokemon, setPokemon] = useState([]);

    useEffect(async ()=> {
        await setPokemonFunction();

    }, [])

    setPokemonFunction= async()=>{
        const arrayPokemones=[]
        for(let i=1; i<=150; i++){
            const response = fetch ('https://pokeapi.co/api/v2/pokemon/'+i)
            const res = await response.json()
            arrayPokemones.push({
                name:res.name,
                id:res.id,
                type: res.types[0].type.name,
                urlImg:res.sprites.front_default
            });
        }
        setPokemon(arrayPokemones)
    }
    return (
        <div className='pokemones'>
            {pokemon.length>0 ?
            pokemon.map(poke=>{
                return(
                    <div>
                        <img src={pok.img}/>
                        <h3>{pok.name}</h3>
                        <p>{pok.type}</p>
                    </div>
                )}
                )
            :
            <p>No hay poquemones</p>
            }
        </div>
    )
}
export default PokemonCard
