import React, { useEffect, useState } from 'react';
import './PokemonCard.css'

const PokemonCard = () => {
    const [pokemon, setPokemon] = useState([]);

    useEffect(async () => {
        await setPokemonFunction();
    }, []);

    const setPokemonFunction = async () => {
        const arrayPokemones = []
        for (let i = 1; i <= 150; i++) {
            const response = await fetch('https://pokeapi.co/api/v2/pokemon/' + i)
            const res = await response.json()
            arrayPokemones.push({
                name: res.name,
                id: res.id,
                type: res.types[0].type.name,
                urlImg: res.sprites.front_default
            });
        }
        setPokemon(arrayPokemones)
    }
    return (
        <div className='pokemones'>
            {pokemon.length > 0 ?
                pokemon.map(pok => {
                    return (
                        <div>
                            <img src={pok.urlImg} alt='' />
                            <h3>{pok.name}</h3>
                            <p>Type: {pok.type}</p>
                            <button id={pok.id}>Add to favs</button>
                        </div>
                    )
                }
                )
                :
                <p>Cargando pokemones...</p>
            }
        </div>
    )
}
export default PokemonCard
