import './PokemonCard.css'



const PokemonCard = () => {
    const[pokemon, setPokemon]=useState([]);
    useEffect(() => {
        const url = 'https://pokeapi.co/api/v2/pokemon/3'
        fetch(url)
            .then(res => res.json())
            .then(data => {
                let item ={
                name: data.name,
                id: data.id
